-- Add image fields to rewards table
ALTER TABLE rewards 
ADD COLUMN IF NOT EXISTS image_url TEXT,
ADD COLUMN IF NOT EXISTS image_name TEXT,
ADD COLUMN IF NOT EXISTS image_type TEXT,
ADD COLUMN IF NOT EXISTS image_size INTEGER;

-- Update existing rewards with sample images (base64 placeholders)
UPDATE rewards SET 
  image_url = 'data:image/svg+xml;base64,',
  image_name = 'food-voucher.svg',
  image_type = 'image/svg+xml',
  image_size = 512
WHERE category = 'food';

